import { ref, onMounted, onBeforeUnmount, nextTick, watch } from 'vue';
import { useRoute } from 'vue-router';
import axios from 'axios';
import 'select2';
import feather from 'feather-icons';
import Sidebar from '/src/components/Admin/Sidebar.vue';
import Navbar from '/src/components/Admin/Navbar.vue';
import Barcode from './Barcode.vue';
export default {
  components: {
    Sidebar,
    Navbar,
    Barcode
  },
  setup() {
    const inputedProduct = ref({
      sku: '',
      name: '',
      description: '',
      price: 0,
      cost: 0,
      category: '',
      subCategory: '',
      unit: '',
      brand: '',
      variant: '',
      discount: 0,
      discountType: '',
      manufacturedDate: '',
      expiryDate: '',
      status: '',
      image: ''
    });
    const skuError = ref('');
    const route = useRoute();
    const product = ref({});
    const getProductDetails = async () => {
      const productId = route.params.id; // Use 'id' to match route param
      try {
        const response = await axios.get(`http://localhost:5000/api/get_product/${productId}`);
        product.value = response.data.product;
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    // Watch product data and populate inputedProduct when loaded
    watch(product, newProduct => {
      if (newProduct) {
        Object.assign(inputedProduct.value, newProduct);
      }
      if (newProduct) {
        inputedProduct.value = {
          ...newProduct,
          image: {
            url: newProduct.imageUrl || ''
          } // Use 'imageUrl' to store image path from backend
        };
      }
    });
    const select = ref('.select');
    onMounted(async () => {
      try {
        await getProductDetails();
        await $(select.value).select2();
        await feather.replace();
      } catch (error) {
        console.log(error);
      }
    });
    onBeforeUnmount(() => {
      $(select.value).select2('destroy');
    });
    const toggleStatus = event => {
      inputedProduct.value.status = event.target.checked ? 'active' : 'inactive';
    };
    const autoGenerateSKU = () => {
      inputedProduct.value.sku = generateEAN13();
    };
    const generateEAN13 = () => {
      let digits = '4';
      for (let i = 0; i < 11; i++) {
        digits += Math.floor(Math.random() * 10).toString();
      }
      let sumOdd = 0;
      let sumEven = 0;
      for (let i = 0; i < 12; i++) {
        const digit = parseInt(digits[i], 10);
        if (i % 2 === 0) {
          sumOdd += digit;
        } else {
          sumEven += digit;
        }
      }
      const totalSum = sumOdd + sumEven * 3;
      const checksum = (10 - totalSum % 10) % 10;
      return digits + checksum;
    };

    // Watch for changes to the SKU and update the barcode
    watch(() => inputedProduct.value.sku, async newSku => {
      if (newSku.length === 13) {
        try {
          // Send the SKU to the backend for existence check
          const response = await axios.get(`http://localhost:5000/api/check_sku/${newSku}`);
          console.log(response.data.exists);
          if (response.data.exists) {
            // SKU already exists, reset the SKU input and show an error message
            inputedProduct.value.sku = ''; // Clear the SKU input
            skuError.value = 'SKU already exists. Please generate a different SKU.'; // Set error message
          } else {
            // SKU does not exist, clear the error and accept the SKU
            skuError.value = ''; // Clear any existing error
          }
        } catch (error) {
          // Handle Axios errors properly
          console.error('Error checking SKU:', error);
          if (error.response) {
            // Backend returned an error (status 400 or 500)
            console.error('Backend error:', error.response.data);
            skuError.value = `${error.response.data.message}`;
          } else {
            // Network or other issues
            skuError.value = 'Network error. Please check your connection.';
          }
        }
      }
    });
    const editProduct = async () => {
      try {
        const formData = new FormData();

        // Manually append each product property
        formData.append('sku', inputedProduct.value.sku);
        formData.append('name', inputedProduct.value.name);
        formData.append('description', inputedProduct.value.description);
        formData.append('price', inputedProduct.value.price);
        formData.append('cost', inputedProduct.value.cost);
        formData.append('category', inputedProduct.value.category);
        formData.append('subCategory', inputedProduct.value.subCategory);
        formData.append('unit', inputedProduct.value.unit);
        formData.append('brand', inputedProduct.value.brand);
        formData.append('variant', inputedProduct.value.variant);
        formData.append('discount', inputedProduct.value.discount);
        formData.append('discountType', inputedProduct.value.discountType);
        formData.append('manufacturedDate', inputedProduct.value.manufacturedDate);
        formData.append('expiryDate', inputedProduct.value.expiryDate);
        formData.append('status', inputedProduct.value.status);

        // Check if there is an image and append it
        if (inputedProduct.value.image.file) {
          formData.append('image', inputedProduct.value.image.file);
        }

        // Debug: Log FormData content (only shows key-value pairs)
        formData.forEach((value, key) => {
          console.log(`${key}: ${value}`);
        });
        const productId = route.params.id; // Get product ID from route parameters

        // Send the PUT request to update the product with the FormData
        const response = await axios.put(`http://localhost:5000/api/edit_products/${productId}`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        // Check if the response was successful
        if (response.data.success) {
          alert('Product updated successfully!');
          await getProductDetails(); // Re-fetch product details
        } else {
          alert('Failed to update product');
        }
      } catch (error) {
        console.error('Error updating product:', error);
        alert('An error occurred while updating the product');
      }
    };
    const onFileChange = event => {
      const file = event.target.files[0];
      if (file) {
        inputedProduct.value.image = {
          file,
          url: URL.createObjectURL(file)
        };
        nextTick(() => feather.replace()); // Ensures icons update, if necessary
      }
    };
    const removeImage = () => {
      if (inputedProduct.image.url) {
        URL.revokeObjectURL(inputedProduct.image.url);
      }
      inputedProduct.image = {
        file: null,
        url: ''
      };
      nextTick(() => feather.replace());
    };
    return {
      inputedProduct,
      select,
      editProduct,
      autoGenerateSKU,
      toggleStatus,
      onFileChange,
      removeImage
    };
  },
  methods: {
    filterTextInput(value) {
      return value.replace(/\D/g, '');
    },
    filterNumInput(value) {
      return value.replace(/[^0-9.]/g, '');
    }
  }
};
