      let digits = '4'; // Start with a fixed number (you can choose a different prefix)
      // Generate 12 random digits
      for (let i = 0; i < 11; i++) {
        digits += Math.floor(Math.random() * 10).toString();
      }
      let sumOdd = 0;
      let sumEven = 0;

      // Loop through the first 12 digits and calculate the sums
      for (let i = 0; i < 12; i++) {
