import feather from 'feather-icons';
import Sidebar from '/src/components/Admin/Sidebar.vue';
import Navbar from '/src/components/Admin/Navbar.vue';
export default {
  components: {
    Sidebar,
    Navbar
  },
  data() {
    return {
      sku: '',
      // Product SKU
      productName: '',
      // Product name
      productDescription: '',
      // Product description
      image: {
        url: ''
      },
      // Image data
      price: 0,
      // Price
      cost: 0,
      // Cost
      discountType: 'None',
      // Discount type (None, Percent, Fixed)
      discountValue: '',
      // Discount value
      unit: 'None',
      // Unit
      category: 'None',
      // Category
      subCategory: 'None',
      // Sub-category
      brand: 'None',
      // Brand
      manufacturedDate: '',
      // Manufacturing date
      expiryDate: '',
      // Expiry date
      variantAttributes: '',
      status: '' // Status
    };
  },
  setup() {
    const select = ref('.select');
    onMounted(async () => {
      try {
        // Initialize the select2 plugin
        await $(select.value).select2();
        // Initialize the feather icons
        await feather.replace();
      } catch (error) {
        console.log(error);
      }
    });
    onBeforeUnmount(() => {
      // Destroy the select2 plugin to avoid memory leaks when component is unmounted
      $(select.value).select2('destroy');
    });
    return {
      select
    };
  },
  methods: {
    filterTextInput(value) {
      return value.replace(/[^a-zA-Z0-9.\-() ]/g, '');
    },
    // Allow only numbers and dots
    filterNumInput(value) {
      return value.replace(/[^0-9.]/g, '');
    },
    generateEAN13() {
      let digits = '';
      for (let i = 0; i < 12; i++) {
        digits += Math.floor(Math.random() * 10).toString();
      }
      let sumOdd = 0;
      let sumEven = 0;
      for (let i = 0; i < 12; i++) {
        const digit = parseInt(digits[i], 10);
        if (i % 2 === 0) {
          sumOdd += digit;
        } else {
          sumEven += digit;
        }
      }
      const totalSum = sumOdd + sumEven * 3;
      const checksum = (10 - totalSum % 10) % 10;

      // Return the complete EAN-13 code
      return digits + checksum;
    },
    autoGenerateSKU() {
      this.sku = this.generateEAN13(); // Set sku to generated EAN-13
    },
    checkSubmit(event) {
      if (!this.canSubmit) {
        event.preventDefault();
      } else {
        console.log('Form submitted');
      }
    },
    onFileChange(event) {
      const file = event.target.files[0];
      if (file) {
        // Create a URL for the uploaded file
        this.image = {
          file: file,
          url: URL.createObjectURL(file)
        };
        // Re-render Feather icons after the image URL is set
        this.$nextTick(() => {
          window.feather.replace();
        });
      }
    },
    // Removes the uploaded image and resets the image data
    removeImage() {
      this.image = {
        file: null,
        url: null
      };
    },
    async addProduct() {
      try {
        // Prepare the form data
        const formData = new FormData();
        formData.append('sku', this.sku);
        formData.append('name', this.productName);
        formData.append('description', this.productDescription);
        formData.append('price', this.price);
        formData.append('cost', this.cost);
        formData.append('category', this.category);
        formData.append('subcategory', this.subCategory);
        formData.append('unit', this.unit);
        formData.append('brand', this.brand);
        formData.append('variant', this.variantAttributes); // Assuming variantAttributes is the variant data
        formData.append('discount', this.discountValue);
        formData.append('discountType', this.discountType);
        formData.append('quantity', this.quantity);
        formData.append('quantityAlert', this.quantityAlert);
        formData.append('manufacturedDate', this.manufacturedDate);
        formData.append('expiryDate', this.expiryDate);
        formData.append('status', this.status);
        formData.append('stockStatus', this.stockStatus);

        // Attach the image if available
        if (this.image.file) {
          formData.append('imageUrl', this.image.file);
        }

        // Send the request to add the product
        const response = await axios.post('http://localhost:5000/api/add_products', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        // Handle the response (success feedback or redirect)
        if (response.data.success === true) {
          alert('Product added successfully!');
        } else if (response.data.success === false) {
          alert(`Failed to add product: ${response.data.message}`);
        } else {
          alert('Unexpected response status.');
        }
      } catch (error) {
        console.error('Error adding product:', error);
        alert('An error occurred. Please try again.');
      }
    },
    watch: {
      checkLength(value, length) {
        if (value.length != length) {}
      }
    },
    mouted() {
      feather.replace();
    }
  }
};
