import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.for-each.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import { ref, onMounted, onBeforeUnmount, nextTick, watch } from 'vue';
import { useRoute } from 'vue-router';
import axios from 'axios';
import 'select2';
import feather from 'feather-icons';
import Sidebar from '/src/components/Admin/Sidebar.vue';
import Navbar from '/src/components/Admin/Navbar.vue';
import Barcode from './Barcode.vue';
export default {
  components: {
    Sidebar,
    Navbar,
    Barcode
  },
  setup() {
    const inputedProduct = ref({
      sku: '',
      name: '',
      description: '',
      price: 0,
      cost: 0,
      category: '',
      subCategory: '',
      unit: '',
      brand: '',
      variant: '',
      discount: 0,
      discountType: '',
      manufacturedDate: '',
      expiryDate: '',
      status: 'inactive',
      image: ''
    });
    const route = useRoute();
    const product = ref({});
    const getProductDetails = async () => {
      const productId = route.params.id; // Use 'id' to match route param
      try {
        const response = await axios.get(`http://localhost:5000/api/get_product/${productId}`);
        product.value = response.data.product;
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    // Watch product data and populate inputedProduct when loaded
    watch(product, newProduct => {
      if (newProduct) {
        Object.assign(inputedProduct.value, newProduct);
      }
    });
    const select = ref('.select');
    onMounted(async () => {
      try {
        await getProductDetails();
        await $(select.value).select2();
        await feather.replace();
      } catch (error) {
        console.log(error);
      }
    });
    onBeforeUnmount(() => {
      $(select.value).select2('destroy');
    });
    const toggleStatus = event => {
      inputedProduct.value.status = event.target.checked ? 'active' : 'inactive';
    };
    const autoGenerateSKU = () => {
      inputedProduct.value.sku = generateEAN13();
    };
    const generateEAN13 = () => {
      let digits = '4';
      for (let i = 0; i < 11; i++) {
        digits += Math.floor(Math.random() * 10).toString();
      }
      let sumOdd = 0;
      let sumEven = 0;
      for (let i = 0; i < 12; i++) {
        const digit = parseInt(digits[i], 10);
        if (i % 2 === 0) {
          sumOdd += digit;
        } else {
          sumEven += digit;
        }
      }
      const totalSum = sumOdd + sumEven * 3;
      const checksum = (10 - totalSum % 10) % 10;
      return digits + checksum;
    };

    // Watch for changes to the SKU and update the barcode
    watch(() => inputedProduct.value.sku, newSku => {
      if (newSku.length === 13) {
        console.log('SKU updated:', newSku);
      }
    });
    const editProduct = async () => {
      try {
        const formData = new FormData();
        for (const key in inputedProduct.value) {
          if (key === 'image' && inputedProduct.value.image.file) {
            formData.append('image', inputedProduct.value.image.file);
          } else {
            formData.append(key, inputedProduct.value[key]);
          }
        }
        const response = await axios.post('http://localhost:5000/api/edit_products', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        formData.forEach((value, key) => {
          console.log(`${key}: ${value}`);
        });
        if (response.data.success) {
          alert('Product added successfully!');
        } else {
          alert('Failed to add product');
        }
      } catch (error) {
        console.error('Error adding product:', error);
        alert('An error occurred while adding the product');
      }
    };
    const onFileChange = event => {
      const file = event.target.files[0];
      if (file) {
        inputedProduct.value.image = {
          file,
          url: URL.createObjectURL(file)
        };
        nextTick(() => {
          feather.replace();
        });
      }
    };
    const removeImage = () => {
      inputedProduct.value.image = {
        url: ''
      };
    };
    return {
      inputedProduct,
      select,
      editProduct,
      autoGenerateSKU,
      toggleStatus,
      onFileChange,
      removeImage
    };
  },
  methods: {
    filterTextInput(value) {
      return value.replace(/\D/g, '');
    },
    filterNumInput(value) {
      return value.replace(/[^0-9.]/g, '');
    }
  }
};
