import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.for-each.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import { ref, onMounted, onBeforeUnmount, nextTick, watch } from 'vue';
import { useRoute, useRouter } from 'vue-router';
import axios from 'axios';
import 'select2';
import feather from 'feather-icons';
import Navbar from '/src/components/Admin/Navbar.vue';
import Barcode from './Barcode.vue';
import Swal from 'sweetalert2';
import $ from 'jquery';
export default {
  components: {
    Navbar,
    Barcode
  },
  setup() {
    const apiURL = process.env.VUE_APP_URL;
    const imageURL = process.env.VUE_APP_IMAGE_URL;
    const inputedProduct = ref({
      sku: '',
      name: '',
      description: '',
      price: 0,
      cost: 0,
      category: '',
      subCategory: '',
      unit: '',
      brand: '',
      variant: '',
      discount: 0,
      discountType: '',
      taxType: 'vatable',
      manufacturedDate: '',
      expiryDate: '',
      status: 'inactive',
      image: {
        file: null,
        url: null
      }
    });
    const skuError = ref(null);
    const route = useRoute();
    const router = useRouter();
    const product = ref({});
    const categories = ref([]);
    const units = ref([]);
    const brands = ref([]);
    const barcodeRef = ref(null);
    const curreSKU = ref(null);
    const triggerDownloadBarcode = () => {
      if (barcodeRef.value) {
        barcodeRef.value.downloadBarcode();
      }
    };
    const getProductDetails = async () => {
      const productId = route.params.id;
      try {
        const response = await axios.get(`${apiURL}/get_product/${productId}`);
        product.value = response.data.product;
        curreSKU.value = response.data.product.sku;
        skuError.value = '';

        // Ensure inputedProduct.value.image is initialized
        if (!inputedProduct.value.image) {
          inputedProduct.value.image = {
            file: null,
            url: null
          }; // Initialize it if it's null
        }

        // Set image URL if exists
        if (product.value.image) {
          inputedProduct.value.image.url = `${imageURL}/uploads/${product.value.image}`;
        }
      } catch (error) {
        Swal.fire('Error', 'Failed to fetch product details', 'error');
      }
    };
    const getSelection = async () => {
      try {
        const categoryResponse = await axios.get(`${apiURL}/get_category_list`);
        const unitResponse = await axios.get(`${apiURL}/get_units_list`);
        const brandResponse = await axios.get(`${apiURL}/brands_list`);
        if (categoryResponse.data.success) {
          categories.value = categoryResponse.data.categories;
        } else {
          Swal.fire('Error', categoryResponse.data.message, 'error');
        }
        if (unitResponse.data.success) {
          units.value = unitResponse.data.unitList;
        } else {
          Swal.fire('Error', unitResponse.data.message, 'error');
        }
        if (brandResponse.data.success) {
          brands.value = brandResponse.data.brandList;
        } else {
          Swal.fire('Error', brandResponse.data.message, 'error');
        }
      } catch (error) {
        Swal.fire('Error', 'Failed to fetch selection data', 'error');
      }
    };
    watch(product, newProduct => {
      if (newProduct) {
        Object.assign(inputedProduct.value, newProduct);
      }
    });
    const select = ref('.select');
    onMounted(async () => {
      try {
        await getProductDetails();
        await getSelection();
        await $(select.value).select2();
        await feather.replace();
      } catch (error) {
        console.log(error);
      }
    });
    onBeforeUnmount(() => {
      $(select.value).select2('destroy');
    });
    const toggleStatus = event => {
      inputedProduct.value.status = event.target.checked ? 'active' : 'inactive';
    };
    const autoGenerateSKU = () => {
      inputedProduct.value.sku = generateEAN13();
    };
    const generateEAN13 = () => {
      let digits = '4';
      for (let i = 0; i < 11; i++) {
        digits += Math.floor(Math.random() * 10).toString();
      }
      let sumOdd = 0;
      let sumEven = 0;
      for (let i = 0; i < 12; i++) {
        const digit = parseInt(digits[i], 10);
        if (i % 2 === 0) {
          sumOdd += digit;
        } else {
          sumEven += digit;
        }
      }
      const totalSum = sumOdd + sumEven * 3;
      const checksum = (10 - totalSum % 10) % 10;
      return digits + checksum;
    };
    watch(() => inputedProduct.value.sku, async newSku => {
      if (newSku.length === 13) {
        try {
          const response = await axios.get(`${apiURL}/check_sku/${newSku}`);
          if (response.data.exists && newSku !== curreSKU.value) {
            skuError.value = 'SKU already exists. Please generate a different SKU.';
            inputedProduct.value.sku = '';
          } else {
            skuError.value = '';
          }
        } catch (error) {
          skuError.value = error.response?.data?.message || 'Network error. Please check your connection.';
        }
      }
    });
    const editProduct = async () => {
      try {
        const formData = new FormData();
        Object.keys(inputedProduct.value).forEach(key => {
          formData.append(key, inputedProduct.value[key]);
        });

        // Append image file if it exists
        if (inputedProduct.value.image && inputedProduct.value.image.file) {
          formData.append('image', inputedProduct.value.image.file);
        }
        const productId = route.params.id;
        const response = await axios.patch(`${apiURL}/edit_products/${productId}`, formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.data.success) {
          Swal.fire('Success', 'Product updated successfully!', 'success');
          await getProductDetails();
        } else {
          Swal.fire('Error', 'Failed to update product', 'error');
        }
      } catch (error) {
        Swal.fire('Error', error.message, 'error');
      }
    };
    const onFileChange = event => {
      const file = event.target.files[0];
      if (file) {
        // Ensure inputedProduct.image is initialized before setting properties
        if (!inputedProduct.value.image) {
          inputedProduct.value.image = {
            file: null,
            url: null
          };
        }

        // Set file and URL for the image
        inputedProduct.value.image.file = file; // Store the file object
        inputedProduct.value.image.url = URL.createObjectURL(file); // Create a preview URL for the image
        nextTick(() => feather.replace()); // Update any feather icons (if needed)
      }
    };
    const removeImage = () => {
      inputedProduct.value.image = {
        file: null,
        url: null
      };
      nextTick(() => feather.replace());
    };
    return {
      inputedProduct,
      select,
      editProduct,
      autoGenerateSKU,
      toggleStatus,
      onFileChange,
      removeImage,
      triggerDownloadBarcode,
      skuError,
      curreSKU,
      imageURL,
      apiURL,
      categories,
      units,
      brands
    };
  },
  methods: {
    filterTextInput(value) {
      return value.replace(/\D/g, '');
    },
    filterNumInput(value) {
      return value.replace(/[^0-9.]/g, '');
    }
  }
};
