        resetTransactions();
        modalController.show('payment-modal');
        modalController.focus('payment-modal');
      }
    };
    const openReturnModal = () => {
      resetTransactions();
      modalController.show('return-modal');
      modalController.focus('return-modal');
      addNavigationListener();
