          // Clear transaction ID input
        } else {
          Swal.fire({
            title: 'Error',
            text: response.data.message || 'Failed to process the return.',
            icon: 'error',
            timer: 2000,
            showConfirmButton: false
          });
        }
      } catch (error) {
