      switch (event.key) {
        case "ArrowDown":
          if (focusedCartIndex.value < items.length - 1) {
            event.preventDefault(); // Prevent scrolling
            focusedCartIndex.value++;
          }
          break;
        case "ArrowUp":
          if (focusedCartIndex.value > 0) {
            event.preventDefault(); // Prevent scrolling
            focusedCartIndex.value--;
          }
          break;
