          icon: "info",
          timer: 1500,
          showConfirmButton: false
        });
        barcodeInput.value.focus();
      }
    };

    // Function to handle canceling the transaction and clearing the cart
    const cancelTransaction = () => {
      // Show SweetAlert for confirmation
      Swal.fire({
        title: "Cancel Transaction",
        text: "Are you sure you want to cancel the transaction and empty the cart?\n press ESC to cancel or ENTER to proceed.",
        icon: "warning",
        showCancelButton: true,
        confirmButtonText: "Proceed",
        cancelButtonText: "Cancel"
      }).then(result => {
        if (result.isConfirmed) {
          // Clear the cart if confirmed
          if (cart.value.length > 0) {
            cart.value = [];
            updateSubtotal(); // Update subtotal after emptying cart
            Swal.fire({
              title: "Transaction Canceled",
              text: "Your cart has been emptied.",
              icon: "success",
              timer: 1000,
              showConfirmButton: false
            });
          } else {
            Swal.fire({
              title: "Your Cart is empty",
              text: "Make new transaction",
              icon: "info",
              timer: 1500,
              showConfirmButton: false
            });
          }
          barcodeInput.value.focus();
        }
      });
    };

    // Assuming `processReturn` and `transactionError` are defined somewhere in your code.

    // Lifecycle Hooks
    onMounted(() => {
      getCategories();
      if (barcodeInput.value) {
        barcodeInput.value.focus();
      }
      window.addEventListener('keydown', handleKeydown); // Listen to keydown events
      window.addEventListener("keydown", handleCartKeyNavigation);
    });
    onBeforeUnmount(() => {
      window.removeEventListener('keydown', handleKeydown);
      window.removeEventListener("keydown", handleCartKeyNavigation);
    });

    // Return Variables and Methods
    return {
      categories,
      selectedCategory,
      barcode,
      barcodeInput,
      cart,
      subtotal,
      VAT,
      discount,
      total,
      barcodeError,
      lastAddedProduct,
      stockQuantity,
      newQuantity,
      quantityError,
      addProduct,
      updateProductQuantity,
      handleModalKeydown,
      focusedIndex,
      handleKeyNavigation,
      validateReturnQuantity,
      focusedCartIndex,
      handleCartKeyNavigation,
      openEditCartModal,
      validateCartQuantity,
      removeCartItem,
      cancelTransaction,
      change,
      clientPayment,
      paymentError,
      selectCategory,
      products,
      imgURL,
      transactionId,
      transactionError,
      returnItems,
      fetchTransactionItems,
      processReturn,
      processVoid,
      openPaymentModal,
      openVoidModal,
      openReturnModal,
      processPayment
    };
  }
