import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.for-each.js";
import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import { ref, onMounted, onBeforeUnmount, nextTick } from 'vue';
import axios from 'axios';
import 'select2';
import feather from 'feather-icons';
import Sidebar from '/src/components/Admin/Sidebar.vue';
import Navbar from '/src/components/Admin/Navbar.vue';
export default {
  components: {
    Sidebar,
    Navbar
  },
  setup() {
    const inputedProduct = ref({
      sku: '',
      productName: '',
      productDescription: '',
      image: {
        url: ''
      },
      price: 0,
      cost: 0,
      discountType: '',
      discountValue: 0,
      unit: '',
      category: '',
      subCategory: '',
      brand: '',
      manufacturedDate: '',
      expiryDate: '',
      variantAttributes: '',
      status: 'inactive',
      imageUrl: ''
    });
    const select = ref('.select');
    onMounted(async () => {
      try {
        // Initialize select2 plugin
        await $(select.value).select2();
        // Initialize feather icons
        await feather.replace();
      } catch (error) {
        console.log(error);
      }
    });
    onBeforeUnmount(() => {
      // Destroy select2 plugin on component unmount
      $(select.value).select2('destroy');
    });
    const toggleStatus = event => {
      inputedProduct.value.status = event.target.checked ? 'active' : 'inactive';
    };
    const autoGenerateSKU = () => {
      inputedProduct.value.sku = generateEAN13();
    };
    const generateEAN13 = () => {
      let digits = '';
      for (let i = 0; i < 12; i++) {
        digits += Math.floor(Math.random() * 10).toString();
      }
      let sumOdd = 0;
      let sumEven = 0;
      for (let i = 0; i < 12; i++) {
        const digit = parseInt(digits[i], 10);
        if (i % 2 === 0) {
          sumOdd += digit;
        } else {
          sumEven += digit;
        }
      }
      const totalSum = sumOdd + sumEven * 3;
      const checksum = (10 - totalSum % 10) % 10;
      return digits + checksum;
    };
    const addProduct = async () => {
      try {
        const {
          sku,
          productName,
          price,
          cost
        } = inputedProduct.value;
        if (!sku || !productName || price === 0 || cost === 0) {
          alert("SKU, Name, Price, and Cost are required.");
          return;
        }
        const formData = new FormData();
        Object.keys(inputedProduct.value).forEach(key => {
          formData.append(key, inputedProduct.value[key]);
        });
        const response = await axios.post('http://localhost:5000/api/add_products', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        if (response.data.success) {
          alert('Product added successfully!');
        } else {
          alert('Failed to add product');
        }
      } catch (error) {
        console.error('Error adding product:', error);
        alert('An error occurred while adding the product');
      }
    };
    return {
      inputedProduct,
      select,
      addProduct,
      autoGenerateSKU,
      toggleStatus
    };
  },
  methods: {
    filterTextInput(value) {
      return value.replace(/[^a-zA-Z0-9.\-() ]/g, '');
    },
    filterNumInput(value) {
      return value.replace(/[^0-9.]/g, '');
    },
    onFileChange(event) {
      const file = event.target.files[0];
      if (file) {
        this.inputedProduct.image = {
          file,
          url: URL.createObjectURL(file)
        };
        nextTick(() => {
          feather.replace();
        });
      }
    },
    removeImage() {
      this.inputedProduct.image = {
        file: null,
        url: null
      };
    }
  }
};
